import React, { Component } from 'react'
import axios from 'axios'
import regUserData from './registration_user_data.json'


class Registration extends Component
{

    constructor(props)
    {
        super(props)

        this.state={ id:'', fname:'', lname:'', email:'', password:'', cpassword:'', location:'' }
    } 


    inputHandler=(e)=>
    {
        this.setState( { ...this.state , [e.target.name] : e.target.value })
    }


    //Get form data after submit
    submitHandler=async (event)=>
    {
        //control page loading after submit Formdata
        event.preventDefault()
        
        await axios.post(`${regUserData}`,this.state).then(()=> this.props.history.push(`${regUserData}`))
    
    }


    render()
    {
    

        return(
            <>
    
                {/*  heading */}
                <div className="py-2 container">
                    <h2 className="py-2"> REGISTRATION FORM </h2>
                </div>
                {/*  heading */}
    
    
                {/*  registration form */}
                <form className="form-group mx-auto w-50 " onSubmit={this.submitHandler} >
    
                    <div>
    
                        <label> ENTER FIRST NAME : </label>
                        <input type="text" value={fname} name="fname" onChange={ this.inputHandler.bind(this)} required />
                    </div>
    
                    <div>
                        <label> ENTER LAST NAME : </label>
                        <input type="text" value={lname} name={lname} onChange={ this.inputHandler.bind(this)} required />
                    </div>
    
                    <div>
                        <label> ENTER EMAIL ID : </label>
                        <input type="email" value={email} name={email} onChange={ this.inputHandler.bind(this)} required />
                    </div>
    
                    <div>
                        <label> ENTER PASSWORD : </label>
                        <input type="password" value={password} name={password} onChange={ this.inputHandler.bind(this)} required />
                    </div>
    
                    <div>
                        <label> CONFIRM PASSWORD : </label>
                        <input type="password" value={cpassword} name={cpassword} onChange={ this.inputHandler.bind(this)} required />
                    </div>
    
                    <div>
                        <label> ENTER LOCATION : </label>
                        <input type="text" value={location} name={location} onChange={ this.inputHandler.bind(this)} required />
                    </div>
                    
    
                </form>
                {/*  registration form */}
    
            </>
        )
    
    }

    
}


export default Registration;
