import React, { Component } from 'react'
import Registration from './Registration.js'




class Login extends Component
{

    constructor(props)
    {
        super(props)
        this.state={email:'', password:''}
    }


    // input textbox collection
    inputHandler=(e)=>
    {
        this.setState({ ...this.state , [e.target.name] : e.target.value } )
    }

    Fun=()=>{ { Registration() } }


    
    render()
    {
        const {email,password}=this.state

        return(
            <>

                <div className="py-2 container">
                    <h2 className="py-2"> LOG IN </h2>
                </div>
    
    
                <form className="mx-auto w-50 " >
    
                    <div className="form-group p-1">
                        <label> ENTER EMAIL ID : </label>
                        <input className="form-control" type="email" 
                        value={email} name="email" onChange={ this.inputHandler.bind(this)} required />
                    </div>
        
                    <div>
                        <label> ENTER PASSWORD : </label>
                        <input className="form-control" type="password" 
                        value={password} name="password" onChange={ this.inputHandler.bind(this)} required />
                    </div>
    
                    <div className="form-group p-1">
                        <button className="btn btn-primary m-3" type="submit" > Submit </button>
                    </div>

                    <div>
                        <button className="btn btn-primary m-3" onClick={Createa()}> Create Account </button>
                    </div>
    
                </form>
            </>
        )
    }
    
}


export default Login;
