import React,{useState,useContext, useEffect} from 'react'
import axios from 'axios';



const Context_data=React.createContext();

const url="https://jsonplaceholder.typicode.com/posts"

const Hooks=()=>
{

    const [userdata,setUserdata]=useState( [] );

    useEffect( ()=>{
        axios.get(url).then( (res)=> setUserdata( res.data ))
    },[])

    return (<>
        <div className="container p-5">

            <h3>Employee</h3>

            {
                userdata.map((d,index)=>
                {
                    return(<> 
                        <ul>
                            <li>{userdata.id}</li>
                            <li>{userdata.title}</li>
                        </ul>
                    </>)
                }) 
            }

            <br />
            <br />

            <Context_data.Provider value={userdata}>

                <div classNmae="container bordered-1"> <Body  /> </div>

            </Context_data.Provider >
                
        </div>
    </>)


    
}


//child component
const Body=()=> 
{

    let body_data=useContext(Context_data);

    const {id,title,body}=body_data

    return(<>

        <div className="container">
            <h3>f Details</h3>

            <li>{id}</li>
            <li>{body}</li>
        </div>


        <br/>
        <br/>

        <Salary />
           
    </>)

}


//child component
const Salary=()=> 
{

    let employee_data=useContext(Context_data);

    const {id,name,location,salary}=employee_data

    return(<>

        <div className="container">

            <h3>Salary Details</h3>

            <li>{id}</li>
            <li>{salary}</li>

        </div>
           
    </>)

}

export default Hooks
