import React, { Component } from 'react'
import axios from 'axios'
import {Link} from 're'


const url="http://localhost:3001/user"

class RestapiData extends Component
{

    constructor(props)
    {

        super(props)

        this.state={ userdata : [] }

    }


    c= async()=>
    {
        await axios.get(url).then((res)=>this.setState( {userdata : res.data } ) ) 
    }


    componentDidMount()
    {
        this.c();
    }


    // delete operation function
    Delete=()=>
    {
        if(window.confirm(` DO YOU WANT TO DELETE ${id} NOW ? `))
        {
            axios.delete(`${url}/${id}`); //deletes selected id 
            this.c();// upadtes the table
        }
    }


    render()
    {

        const {fname,lname,location,email}=this.props.state

        return(<>
            
            <table>

                {
                    userdata.map((x,index)=>{
                        return(<>
                            <tr key={index}>
                                <th>S.NO</th>
                                <th>NAME</th>
                                <th>EMAIL</th>
                                <th>LOCATION</th>
                                <th colspan="3" >ACTION</th>
                            </tr>
                            <tr>
                                <td>{x.fname} {x.lname}</td>
                                <td>{x.email}</td>
                                <td>{x.location}</td>
                                <td> <Link to={`user/${x.id}`} className="btn btn-primary">View</Link> </td>
                                <td> <button className="btn btn-info">Edit</button> </td>
                                <td> <button onClick={ ()=> this.Delete(x.id) } className="btn btn-danger">Delete</button> </td>
                            </tr>
                        </>)
                    })
                }

            </table>

        </>)

    }
    

}


export default RestapiData;
